static void test_map_update()
{
    switch (game_state)
    {
    case START:
        if (input_get_all() != 0x0) {
            game_state = MAIN;
            switch_room(0,0);
        }
        break;

    case MAIN:
        player_update(input_get_all());
        enemy_update();

        if (input_get_all() & KEY_PAUSE) {
            game_state = PAUSE;
        }
        break;

    case PAUSE:
        if (input_get_all() & KEY_PAUSE) {
            game_state = MAIN;
        }
        break;

    case DEATH:
        if (input_get_all() & KEY_PAUSE) {
            test_map_init();
            game_state = START;
        }
        break;
    }
}

static void test_map_draw()
{
    switch (game_state)
    {
    case START:
        start_menu();
        break;

    case MAIN:
    //    clear_screen(BLACK);
     //   map_draw(0, 0);
        player_draw();
        enemies_draw();
        break;

    case PAUSE:
        pause_menu();
        break;

    case DEATH:
        death_menu();
        break;
    }
}

void test_map()
{
    pico_init(test_map_init, test_map_update, test_map_draw);
}
